Ext.define('Painometer.controller.AppMainController', {
    extend: 'Ext.app.Controller',

    config: {

    },

    isLandscape: function() {
        Ext.Viewport.on({
            scope: this,
            orientationchange: function(viewport, newOrientation, width, height) {
                vw = viewport; no = newOrientation; wi = width; he = height;
                if (no == "landscape"){
                    return "true"; 
                }
                else{
                    return "false";
                }
            }
        });

    },

    isPortrait: function() {
        Ext.Viewport.on({
            scope: this,
            orientationchange: function(viewport, newOrientation, width, height) {
                vw = viewport; no = newOrientation; wi = width; he = height;
                if (no == "portrait"){
                    return "true"; 
                }
                else{
                    return "false";
                }
            }
        });
    }

});